package guiuebung;

import javax.imageio.ImageIO;
import javax.swing.*;
import java.awt.*;
import java.awt.image.BufferedImage;
import java.io.IOException;
import java.net.URL;

public class MainView extends JFrame {
    private final String imagelink = "data:image/jpeg;base64,";
    private final String link = "https://de.wikipedia.org/wiki/Wikipedia:Hauptseite";
    private JLabel companieImage;
    private JTextField searchBar;
    private JButton search;
    private JPanel searchPanel;
    private JPanel northPanel;
    public MainView(){
        super("Main View");
        setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
        init();
        pack();
        setVisible(true);
    }

    private void init() {
        searchBar = new JTextField();
        search = new JButton("Suchen");
        searchPanel =  new JPanel();
        searchPanel.setLayout(new GridLayout(2,0));
        searchPanel.add(searchBar);
        searchPanel.add(search);

        northPanel = new JPanel();
        northPanel.setLayout(new BorderLayout());


        try {
            URL url = new URL(imagelink);
            BufferedImage image = ImageIO.read(url);
            companieImage = new JLabel(new ImageIcon(image));
        }catch (IOException e) {
            e.printStackTrace();
        }
    }
}
